	function replaceStr(obj, type, num) {
	  /*字符串很长的做一下替换*/
	  num = num || 6;
	  var txt;
	  var l = 0;
	  if (type == 'text') txt = $(obj).text();else txt = $(obj).val();
	  for (var i = 0; i < txt.length; i++) {
	    var t = new RegExp('[a-z0-9/]').test(txt[i]);
	    if (t) l += 0.5;else l += 1;
	    if (l > num) {
	      txt = txt.slice(0, i) + '...';
	      if (type == 'text') $(obj).text(txt);else $(obj).val(txt);
	      return;
	    }
	  }
	}

